mapper.select:
SELECT BIDID, BIDPRICE, BIDDATE, USERID, AUCTIONID
FROM BID
WHERE BIDID = #{param1}

SELECT BIDID, BIDPRICE, BIDDATE, USERID, AUCTIONID
FROM BID
WHERE BIDID = #{param1}
AND AUCTIONID = #{param2}

SELECT BIDID, BIDPRICE, BIDDATE, USERID, AUCTIONID
FROM BID
WHERE BIDPRICE = #{param1} AND AUCTIONID = #{param2}

SELECT *
FROM BID
WHERE USERID = #{param1} AND AUCTIONID = #{param2}

mapper.update:
UPDATE BID
SET BIDPRICE = #{bidPrice}
WHERE bidId = #{bidId}
