mapper.select:
SELECT postId, title, communityDate, content_, userId
FROM Community 
WHERE postId = #{param1}

	SELECT postId, title, communityDate, content_, userId
  FROM Community 
  WHERE userId = #{userId}

mapper.update:
UPDATE Community SET
	title = #{title},
	communityDate = #{communityDate},
	content_ = #{content_}
WHERE postId = #{postId}
